    then(cb: (component: this) => any): this;
    /**
     * Sets the disabled state of the component.
     *
     * @param disabled - Whether to disable the component.
     * @returns The component instance.
     *
     * @example
     * ```ts
     * component.setDisabled(true);
     * ```
     *
     * @public
     */
